import React from 'react';
import { HorizontalBar } from 'react-chartjs-2';

/* Gráfico da pergunta 4 GERAL */

const GquatroGeral = () => {
    return(
        <div className="gquatro">
            <HorizontalBar 
                data={{
                    labels: ["Sim", "Não", "Não Responderam"],
                    datasets: [
                        {
                            label: '',
                            data: [623, 706, 238],
                            backgroundColor: '#EEAD2D',
                            barThickness: 'flex',
                            hoverBackgroundColor: "#b8831c",
                        },
                    ],
                }}
                height={300}
                options={{
                    maintainAspectRatio: false,
                    layout: {
                        padding: 60,
                    },

                    legend: {
                        display: false,
/*                         labels: {
                            fontSize: 15,
                            fontColor: "#e6e7ea",
                            fontFamily: 'Roboto',
                        },
*/
                    },

                    title: {
                        display: true,
                        padding: 20,
                        text: "Nº de Respondentes",
                        fontSize: 25,
                        fontColor: "#e6e7ea",
                        fontFamily: 'Roboto',
                    },

                    scales: {
                        yAxes:[
                            {
                                barPercentage: 1,

                                gridLines: {
                                    color: "rgba(0, 0, 0, 0)",
                                },

                                ticks: {
                                    beginAtZero: true,
                                    fontSize: 14,
                                    fontColor: "#e6e7ea",
                                    fontFamily: 'Roboto',
                                },

                                barThickness: 23,
                            },
                        ],

                        xAxes:[
                            {
                                display: false,
                                gridLines: {
                                    color: "rgba(0,0,0,0)",
                                },
                            }
                        ],
                    },
                }}
            />
        </div>
    )
};

export default GquatroGeral;
